import { Component } from 'react'
import Section from "./Section"
import Color from "./Color"
import Headers from "./Headers"
import Paragraph from "./Paragraph"

import Menu from "./Menu"

class Container extends Component {
    constructor(props) {
        super(props)

        this.state = {
            font: 'sans-serif',
            color: [
                'rgba(255,0,0,.3',
                'rgba(255,0,0,.8)',
                'red'  
            ]
        }

        this.changeColor = this.changeColor.bind(this)
    }

    changeFont = (event) => {    
        this.setState({
            font: event.target[event.target.selectedIndex].value
        })
    }

    changeColor(e) {
        console.log(e.target.value)
        let col = []
        for(let i=1; i < e.target.value.length; i+=2 ) {
            let val1 = parseInt(e.target.value[i])
            let val2 = parseInt(e.target.value[i+1])
            if(isNaN(val1)){
                switch(e.target.value[i]) {
                    case "a": val1 = 10;
                    break;
                    case "b": val1 = 11;
                    break;
                    case "c": val1 = 12;
                    break;
                    case "d": val1 = 13;
                    break;
                    case "e": val1 = 14;
                    break;
                    case "f": val1 = 15;
                    break;
                }

                val1 = val1 * 16
            }
            if(isNaN(val2)){
                switch(e.target.value[i+1]) {
                    case "a": val2 = 10;
                    break;
                    case "b": val2 = 11;
                    break;
                    case "c": val2 = 12;
                    break;
                    case "d": val2 = 13;
                    break;
                    case "e": val2 = 14;
                    break;
                    case "f": val2 = 15;
                    break;
                }
            }

            col.push(val1 + val2)
        }

        console.log(col)

        this.setState({
            color: [
                `rgba(${col[0]}, ${col[1]}, ${col[2]},.3`,
                `rgba(${col[0]}, ${col[1]}, ${col[2]},.8)`,
                e.target.value 
            ]
        })
    }

    render() {
        return (
            <>
                <Menu changeFont={this.changeFont} changeColor={this.changeColor}/>
                <Section title="01. Typography">
                    <Headers font={this.state.font}/>
                    <Paragraph font={this.state.font}/>
                </Section>
                <Section title="02. Color">
                    <Color colors={["white", "rgba(0,0,0, .19)", "rgba(0,0,0, .39)", "rgba(0,0,0, .59)", "rgba(0,0,0, .79)", "rgb(0,0,0)"]} />
                    <Color colors={this.state.color} />
                </Section>
            </>
        )
    }
}

export default Container
